with open("saved_torques.pkl", "rb") as f:
    torque1, torque2, torque3, time = pickle.load(f)


matplotlib.rc('font', **font)
time = np.linspace(0,time,len(torque1))

plt.scatter(time, torque1, c='r')
plt.scatter(time, torque2, c='b')
plt.scatter(time, torque3, c='g')


plt.title(r'Stepper Torques')
plt.xlabel(r'Time')
plt.ylabel('Torque(Nm)')

plt.xlim(0,max(time))
plt.ylim(0,max(torque1)+1)
plt.show()
